import{_ as e,o,c as t,O as n}from"./chunks/framework.9482e208.js";const b=JSON.parse(`{"title":"§ 22.1-161 Loan declared indebtedness of the county, city or town; lien on locality's funds created.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-161.md","filePath":"vacode/22.1-161.md"}`),a={name:"vacode/22.1-161.md"},r=n('<h1 id="§-22-1-161-loan-declared-indebtedness-of-the-county-city-or-town-lien-on-locality-s-funds-created" tabindex="-1">§ 22.1-161 Loan declared indebtedness of the county, city or town; lien on locality&#39;s funds created. <a class="header-anchor" href="#§-22-1-161-loan-declared-indebtedness-of-the-county-city-or-town-lien-on-locality-s-funds-created" aria-label="Permalink to &quot;§ 22.1-161 Loan declared indebtedness of the county, city or town; lien on locality&#39;s funds created.&quot;">​</a></h1><p>Any bonds or notes of a school board held by the Literary Fund are hereby declared to be valid and legally binding indebtedness of the county, city or combination thereof constituting the school division or of the town if the town constitutes the school division. There shall be a lien in favor of the Literary Fund on all funds and income of the county, city or town for the amount of such bonds and notes. Therefore, Literary Fund loans are considered to be general obligation debt of the governing body as defined in § <a href="http://law.lis.virginia.gov/vacode/15.2-2602/" target="_blank" rel="noreferrer">15.2-2602</a>, and are subject to the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/15.2-2638/" target="_blank" rel="noreferrer">15.2-2638</a> and <a href="http://law.lis.virginia.gov/vacode/15.2-2659/" target="_blank" rel="noreferrer">15.2-2659</a>. The provisions of this section shall not be affected by the release of any memorandum of lien pursuant to subsection B of § <a href="http://law.lis.virginia.gov/vacode/22.1-151/" target="_blank" rel="noreferrer">22.1-151</a>.</p><p>Code 1950, §§ 22-102, 22-115; 1956, c. 184; 1971, Ex. Sess., c. 128; 1980, c. 559; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0121" target="_blank" rel="noreferrer">121</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0365" target="_blank" rel="noreferrer">365</a>.</p>',3),i=[r];function d(s,l,c,f,h,_){return o(),t("div",null,i)}const g=e(a,[["render",d]]);export{b as __pageData,g as default};
