import{_ as e,o as r,c as t,O as o}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 22.1-17.1 Regulations for reenrollment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-17.1.md","filePath":"vacode/22.1-17.1.md"}'),a={name:"vacode/22.1-17.1.md"},n=o('<h1 id="§-22-1-17-1-regulations-for-reenrollment" tabindex="-1">§ 22.1-17.1 Regulations for reenrollment. <a class="header-anchor" href="#§-22-1-17-1-regulations-for-reenrollment" aria-label="Permalink to &quot;§ 22.1-17.1 Regulations for reenrollment.&quot;">​</a></h1><p>The Board of Education, in cooperation with the Board of Juvenile Justice, shall promulgate regulations for the reenrollment in the public schools of children who have been in the custody of the Department of Juvenile Justice. Such regulations shall include the components required in a reenrollment plan and shall provide for consistency in the curricula, standards and policies between the educational programs required by this title, and those of the Board of Juvenile Justice.</p><p>1990, c. 797; 1991, c. 295; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1000" target="_blank" rel="noreferrer">1000</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',3),i=[n];function l(s,c,h,d,u,_){return r(),t("div",null,i)}const g=e(a,[["render",l]]);export{p as __pageData,g as default};
