import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 22.1-17.3 Identification of student internship programs.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-17.3.md","filePath":"vacode/22.1-17.3.md"}'),o={name:"vacode/22.1-17.3.md"},n=r('<h1 id="§-22-1-17-3-identification-of-student-internship-programs" tabindex="-1">§ 22.1-17.3 Identification of student internship programs. <a class="header-anchor" href="#§-22-1-17-3-identification-of-student-internship-programs" aria-label="Permalink to &quot;§ 22.1-17.3 Identification of student internship programs.&quot;">​</a></h1><p>The Board of Education, the Department of Labor and Industry, and the State Board for Community Colleges shall identify High School to Work Partnerships established pursuant to subsection D of § <a href="/vacode/22.1-227.1/">22.1-227.1</a> and other student internship programs that may be eligible for exemptions from federal and state labor laws and regulations for which exemptions are available for student apprenticeship programs. The Board of Education, the Department of Labor and Industry, and the State Board for Community Colleges shall also establish procedures by which such exemptions may be obtained for such High School to Work Partnerships and other student internship programs.</p><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0150" target="_blank" rel="noreferrer">150</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0142" target="_blank" rel="noreferrer">142</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0388" target="_blank" rel="noreferrer">388</a>.</p>',3),i=[n];function s(d,h,l,p,c,f){return t(),a("div",null,i)}const g=e(o,[["render",s]]);export{u as __pageData,g as default};
