import{_ as e,o as r,c as a,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 22.1-199.2 Standards for remediation programs established.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-199.2.md","filePath":"vacode/22.1-199.2.md"}'),t={name:"vacode/22.1-199.2.md"},n=i('<h1 id="§-22-1-199-2-standards-for-remediation-programs-established" tabindex="-1">§ 22.1-199.2 Standards for remediation programs established. <a class="header-anchor" href="#§-22-1-199-2-standards-for-remediation-programs-established" aria-label="Permalink to &quot;§ 22.1-199.2 Standards for remediation programs established.&quot;">​</a></h1><p>The Board of Education shall promulgate regulations for establishing standards for remediation programs that receive state funding, without regard to state funding designations, which shall be designed to strengthen and improve the effectiveness of such programs in increasing the scholastic achievement of students with academic deficiencies. Such standards shall require school divisions to evaluate remediation programs, annually, in terms of the pass rate on the Standards of Learning tests and the demographic and educational characteristics of students who have been identified for remediation pursuant to subsection C of § <a href="http://law.lis.virginia.gov/vacode/22.1-253.13:1/" target="_blank" rel="noreferrer">22.1-253.13:1</a>, or clause (ii) of subsection A of § <a href="http://law.lis.virginia.gov/vacode/22.1-254/" target="_blank" rel="noreferrer">22.1-254</a>, and § <a href="http://law.lis.virginia.gov/vacode/22.1-254.01/" target="_blank" rel="noreferrer">22.1-254.01</a>.</p><p>The Board shall also establish in regulations, a formula for determining the level of funding necessary to assist school divisions in providing transportation services to students required to attend remediation programs.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0466" target="_blank" rel="noreferrer">466</a>; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0602" target="_blank" rel="noreferrer">602</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0627" target="_blank" rel="noreferrer">627</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0902" target="_blank" rel="noreferrer">902</a>; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0488" target="_blank" rel="noreferrer">488</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0537" target="_blank" rel="noreferrer">537</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0552" target="_blank" rel="noreferrer">552</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0697" target="_blank" rel="noreferrer">697</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0041" target="_blank" rel="noreferrer">41</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0834" target="_blank" rel="noreferrer">834</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0061" target="_blank" rel="noreferrer">61</a>.</p>',4),o=[n];function s(l,g,d,f,h,c){return r(),a("div",null,o)}const _=e(t,[["render",s]]);export{v as __pageData,_ as default};
