import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 22.1-200.1 Optional education programs for kindergarten through grade five.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-200.1.md","filePath":"vacode/22.1-200.1.md"}'),i={name:"vacode/22.1-200.1.md"},o=t('<h1 id="§-22-1-200-1-optional-education-programs-for-kindergarten-through-grade-five" tabindex="-1">§ 22.1-200.1 Optional education programs for kindergarten through grade five. <a class="header-anchor" href="#§-22-1-200-1-optional-education-programs-for-kindergarten-through-grade-five" aria-label="Permalink to &quot;§ 22.1-200.1 Optional education programs for kindergarten through grade five.&quot;">​</a></h1><p>In lieu of §§ <a href="http://law.lis.virginia.gov/vacode/22.1-277/" target="_blank" rel="noreferrer">22.1-277</a>, <a href="http://law.lis.virginia.gov/vacode/22.1-277.07/" target="_blank" rel="noreferrer">22.1-277.07</a>, and <a href="http://law.lis.virginia.gov/vacode/22.1-277.08/" target="_blank" rel="noreferrer">22.1-277.08</a>, a school board may establish optional age-appropriate education programs for young students in grades kindergarten through five who require guidance, supervision, and discipline in a structured learning environment and who need to be redirected toward appropriate classroom decorum and acceptable personal behavior. The programs shall provide instructional and support services that will enable students to maintain academic achievement, attain basic skills and academic proficiencies, and otherwise benefit from a public education during the time that they may be removed from the regular classroom. The programs shall also be designed to accommodate students within the school building to which they have been assigned, facilitate the efficient transition of students between the optional education program and their regular classroom, and provide for the continuity of instruction, a nurturing environment, necessary guidance and supervision, and the participation of the student&#39;s parents in correcting his behavior. Such programs shall be adequately staffed by licensed teachers or other persons with demonstrated qualifications to instruct and manage students with a range of academic gifts and deficiencies, disciplinary problems, and the need to develop and use appropriate social skills.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0846" target="_blank" rel="noreferrer">846</a>.</p>',3),n=[o];function s(d,c,l,h,p,g){return a(),r("div",null,n)}const m=e(i,[["render",s]]);export{f as __pageData,m as default};
