import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 22.1-204.2 Hunter safety education programs for students in grades seven through 12.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-204.2.md","filePath":"vacode/22.1-204.2.md"}'),o={name:"vacode/22.1-204.2.md"},s=r('<h1 id="§-22-1-204-2-hunter-safety-education-programs-for-students-in-grades-seven-through-12" tabindex="-1">§ 22.1-204.2 Hunter safety education programs for students in grades seven through 12. <a class="header-anchor" href="#§-22-1-204-2-hunter-safety-education-programs-for-students-in-grades-seven-through-12" aria-label="Permalink to &quot;§ 22.1-204.2 Hunter safety education programs for students in grades seven through 12.&quot;">​</a></h1><p>A. Local school boards may provide after-school hunter safety education programs for students in the school division in grades seven through 12. Each student shall bear the cost of participating in such programs. Local school boards shall display information on its after-school hunter safety education programs in each school and distribute information to the parents of each student in the school division in grades seven through 12.</p><p>B. The Department of Wildlife Resources shall establish a uniform curriculum for such hunter safety education programs. Each such program shall be taught by a hunter safety instructor certified pursuant to § <a href="/vacode/29.1-300.2/">29.1-300.2</a>.</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0560" target="_blank" rel="noreferrer">560</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0958" target="_blank" rel="noreferrer">958</a>.</p>',4),n=[s];function i(h,c,u,d,l,f){return t(),a("div",null,n)}const _=e(o,[["render",i]]);export{g as __pageData,_ as default};
