import{_ as t,o as i,c as o,z as e,a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 22.1-206.1 Lyme disease; instructional resources and materials.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-206.1.md","filePath":"vacode/22.1-206.1.md"}'),s={name:"vacode/22.1-206.1.md"},r=e("h1",{id:"§-22-1-206-1-lyme-disease-instructional-resources-and-materials",tabindex:"-1"},[a("§ 22.1-206.1 Lyme disease; instructional resources and materials. "),e("a",{class:"header-anchor",href:"#§-22-1-206-1-lyme-disease-instructional-resources-and-materials","aria-label":'Permalink to "§ 22.1-206.1 Lyme disease; instructional resources and materials."'},"​")],-1),n=e("p",null,"The Secretary of Education, in collaboration with the Secretary of Health and Human Resources and the Secretary of Natural and Historic Resources, shall develop instructional resources and materials to assist school boards and local and regional public libraries in establishing an education and awareness program to protect children from Lyme disease and other tick-borne infections. Such instructional resources and materials (i) shall be appropriate for individuals of school age; (ii) shall provide information on the identification of ticks, recommended procedures for safe tick removal, and best practices to provide protection from ticks; (iii) may include video productions, pamphlets, and demonstration programs to illustrate the sizes of various ticks, including sizes before and after each variety has become engorged, to assist with the identification of a tick and the reaction on the skin that may result from a tick bite; and (iv) shall be made available to school boards and local and regional public libraries upon request at no charge.",-1),c=e("p",null,[a("2022, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0303",target:"_blank",rel:"noreferrer"},"303"),a(".")],-1),l=[r,n,c];function d(h,u,m,p,f,_){return i(),o("div",null,l)}const g=t