import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 22.1-207.4 Nutritional guidelines for competitive foods.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-207.4.md","filePath":"vacode/22.1-207.4.md"}'),r={name:"vacode/22.1-207.4.md"},a=i('<h1 id="§-22-1-207-4-nutritional-guidelines-for-competitive-foods" tabindex="-1">§ 22.1-207.4 Nutritional guidelines for competitive foods. <a class="header-anchor" href="#§-22-1-207-4-nutritional-guidelines-for-competitive-foods" aria-label="Permalink to &quot;§ 22.1-207.4 Nutritional guidelines for competitive foods.&quot;">​</a></h1><p>A. For purposes of this section, &quot;competitive food&quot; means any food, excluding beverages, sold to students on school grounds during regular school hours that is not part of the school breakfast or school lunch program.</p><p>B. The Board, in cooperation with the Department of Health, shall promulgate regulations setting nutritional guidelines for all competitive foods.</p><p>C. The Board, with assistance from the Department of Health, shall periodically review the nutritional guidelines for competitive foods to ensure that they remain current and science-based and shall also review the nutritional guidelines upon changes to federal law or regulations for competitive foods.</p><p>D. The regulations promulgated pursuant to this section shall include but not be limited to: calorie, fat, sugar, and sodium content.</p><p>E. The regulations promulgated pursuant to this section shall permit each public school to conduct on school grounds during regular school hours no more than 30 school-sponsored fundraisers per school year, during which food that does not meet the nutrition guidelines for competitive foods may be sold to students.</p><p>2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0718" target="_blank" rel="noreferrer">718</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0568" target="_blank" rel="noreferrer">568</a>.</p>',7),n=[a];function s(l,c,d,u,h,p){return o(),t("div",null,n)}const m=e(r,[["render",s]]);export{g as __pageData,m as default};
