import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 22.1-214.2 Definition of \\"supervise\\" as related to educational programs provided for or by Department of Behavioral Health and Developmental Services.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-214.2.md","filePath":"vacode/22.1-214.2.md"}'),i={name:"vacode/22.1-214.2.md"},o=r('<h1 id="§-22-1-214-2-definition-of-supervise-as-related-to-educational-programs-provided-for-or-by-department-of-behavioral-health-and-developmental-services" tabindex="-1">§ 22.1-214.2 Definition of &quot;supervise&quot; as related to educational programs provided for or by Department of Behavioral Health and Developmental Services. <a class="header-anchor" href="#§-22-1-214-2-definition-of-supervise-as-related-to-educational-programs-provided-for-or-by-department-of-behavioral-health-and-developmental-services" aria-label="Permalink to &quot;§ 22.1-214.2 Definition of &quot;supervise&quot; as related to educational programs provided for or by Department of Behavioral Health and Developmental Services.&quot;">​</a></h1><p>For the purposes of subsection F of § <a href="http://law.lis.virginia.gov/vacode/22.1-214/" target="_blank" rel="noreferrer">22.1-214</a> as related to the educational programs provided for or by the Department of Behavioral Health and Developmental Services, &quot;supervise&quot; shall mean providing active support in (i) designing mechanisms for maintaining constant direct contact and the sharing of ideas, approaches and innovations between the Department of Behavioral Health and Developmental Services and the facility staff responsible for providing educational services; (ii) providing consistent oversight, with particular attention to the mental health programs, to ensure that the availability of educational resources and the distribution of funds clearly reflect the needs of the different student populations residing in the various facilities; (iii) developing guidelines, in cooperation with the Department of Behavioral Health and Developmental Services for the evaluation of the performance of the education directors or other education supervisors employed by the Department of Behavioral Health and Developmental Services; (iv) developing and implementing, in cooperation with the Department of Behavioral Health and Developmental Services, programs to ensure that the educational and treatment needs of dually diagnosed children in state facilities are met; and (v) ensuring that the expertise of the Department of Education is utilized by providing technical assistance to the education programs provided for or by the Department of Behavioral Health and Developmental Services in the areas of selection and acquisition of educational materials, curriculum development including career and technical education, when appropriate, and applications for federal grants.</p><p>1985, c. 207; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0483" target="_blank" rel="noreferrer">483</a>; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>.</p>',3),n=[o];function l(s,d,p,c,h,f){return a(),t("div",null,n)}const g=e(i,[["render",l]]);export{u as __pageData,g as default};
