import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 22.1-221 Transportation of children with disabilities attending public or private special education programs.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-221.md","filePath":"vacode/22.1-221.md"}'),i={name:"vacode/22.1-221.md"},o=r('<h1 id="§-22-1-221-transportation-of-children-with-disabilities-attending-public-or-private-special-education-programs" tabindex="-1">§ 22.1-221 Transportation of children with disabilities attending public or private special education programs. <a class="header-anchor" href="#§-22-1-221-transportation-of-children-with-disabilities-attending-public-or-private-special-education-programs" aria-label="Permalink to &quot;§ 22.1-221 Transportation of children with disabilities attending public or private special education programs.&quot;">​</a></h1><p>A. Each disabled child enrolled in and attending a special education program provided by the school division pursuant to any of the provisions of § <a href="http://law.lis.virginia.gov/vacode/22.1-216/" target="_blank" rel="noreferrer">22.1-216</a> or § <a href="http://law.lis.virginia.gov/vacode/22.1-218/" target="_blank" rel="noreferrer">22.1-218</a> shall be entitled to transportation to and from such school or class at no cost if such transportation is necessary to enable such child to obtain the benefit of educational programs and opportunities.</p><p>B. A school board may, in lieu of providing transportation on an approved school bus, allot funds to pay the reasonable cost of special arrangement transportation. The Board of Education shall reimburse the school board sixty percent of such cost if funds therefor are available.</p><p>C. Costs for operating approved school buses which are equipped or used primarily for transporting children with disabilities shall be reimbursed according to the regulations promulgated by the Board of Education from such state funds as are appropriated for this purpose.</p><p>Code 1950, § 22-10.11; 1974, c. 480; 1975, cc. 464, 513; 1978, c. 386; 1980, c. 559; 1983, c. 521; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0854" target="_blank" rel="noreferrer">854</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0123" target="_blank" rel="noreferrer">123</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0247" target="_blank" rel="noreferrer">247</a>.</p>',5),n=[o];function s(l,c,p,d,h,f){return a(),t("div",null,n)}const b=e(i,[["render",s]]);export{g as __pageData,b as default};
