import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 22.1-224 Duties of State Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-224.md","filePath":"vacode/22.1-224.md"}'),i={name:"vacode/22.1-224.md"},r=o('<h1 id="§-22-1-224-duties-of-state-board" tabindex="-1">§ 22.1-224 Duties of State Board. <a class="header-anchor" href="#§-22-1-224-duties-of-state-board" aria-label="Permalink to &quot;§ 22.1-224 Duties of State Board.&quot;">​</a></h1><p>The Board of Education shall:</p><ol><li><p>Require the development of adult education programs in every school division;</p></li><li><p>Encourage coordination in the development and provision of adult education programs between school boards and other state, federal, and local public and private agencies;</p></li><li><p>Promulgate appropriate standards and guidelines for adult education programs;</p></li><li><p>Accept and administer grants, gifts, services, and funds from available sources for use in adult education programs; and</p></li><li><p>Assist school divisions with all diligence in meeting the educational needs of individuals participating in adult education programs to master the requirements for and earn a high school diploma or to pass a high school equivalency examination approved by the Board of Education.</p></li></ol><p>Code 1950, § 22-361; 1978, c. 522; 1980, c. 559; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0564" target="_blank" rel="noreferrer">564</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0084" target="_blank" rel="noreferrer">84</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0717" target="_blank" rel="noreferrer">717</a>.</p>',4),n=[r];function s(d,l,c,p,u,f){return a(),t("div",null,n)}const _=e(i,[["render",s]]);export{g as __pageData,_ as default};
