import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 22.1-23 Duties in general.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-23.md","filePath":"vacode/22.1-23.md"}'),o={name:"vacode/22.1-23.md"},t=r('<h1 id="§-22-1-23-duties-in-general" tabindex="-1">§ 22.1-23 Duties in general. <a class="header-anchor" href="#§-22-1-23-duties-in-general" aria-label="Permalink to &quot;§ 22.1-23 Duties in general.&quot;">​</a></h1><p>The Superintendent of Public Instruction shall:</p><ol><li><p>Serve as secretary of the Board of Education;</p></li><li><p>Provide such assistance in his office as shall be necessary for the proper and uniform enforcement of the provisions of the school laws in cooperation with the local school authorities;</p></li><li><p>Prepare and furnish such forms for attendance officers, teachers and other school officials as are required by law;</p></li><li><p>(Expires July 1, 2025) At least annually, survey all local school divisions to identify critical shortages of (i) teachers and administrative personnel by geographic area, by school division, or by subject matter and (ii) school bus drivers by geographic area and local school division and report such critical shortages to each local school division and to the Virginia Retirement System;</p></li><li><p>Develop and provide to local school divisions a model exit questionnaire for teachers;</p></li><li><p>Along with the State Health Commissioner, work to combat childhood obesity and other chronic health conditions that affect school-age children;</p></li><li><p>Designate an employee of the Department of Education to serve as its liaison to the State Council of Higher Education for Virginia and the State Board for Community Colleges; and</p></li><li><p>Perform such other duties as the Board of Education may prescribe.</p></li></ol><p>Code 1950, §§ 22-25, 22-26, 22-28; 1980, c. 559; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0689" target="_blank" rel="noreferrer">689</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0700" target="_blank" rel="noreferrer">700</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0043" target="_blank" rel="noreferrer">43</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0055" target="_blank" rel="noreferrer">55</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0140" target="_blank" rel="noreferrer">140</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0594" target="_blank" rel="noreferrer">594</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0379" target="_blank" rel="noreferrer">379</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0437" target="_blank" rel="noreferrer">437</a>.</p>',4),l=[t];function n(s,c,h,p,f,d){return i(),a("div",null,l)}const _=e(o,[["render",n]]);export{u as __pageData,_ as default};
