import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 22.1-238 Approval of textbooks.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-238.md","filePath":"vacode/22.1-238.md"}'),r={name:"vacode/22.1-238.md"},s=a('<h1 id="§-22-1-238-approval-of-textbooks" tabindex="-1">§ 22.1-238 Approval of textbooks. <a class="header-anchor" href="#§-22-1-238-approval-of-textbooks" aria-label="Permalink to &quot;§ 22.1-238 Approval of textbooks.&quot;">​</a></h1><p>A. The Board of Education shall have the authority to approve textbooks suitable for use in the public schools and shall have authority to approve instructional aids and materials for use in the public schools. The Board shall publish a list of all approved textbooks on its website and shall list the publisher and the current lowest wholesale price of such textbooks.</p><p>B. Any school board may use textbooks not approved by the Board provided the school board selects such books in accordance with regulations promulgated by the Board.</p><p>C. For the purposes of this chapter, the term &quot;textbooks&quot; means print or electronic media for student use that serve as the primary curriculum basis for a grade-level subject or course.</p><p>1980, c. 559; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0421" target="_blank" rel="noreferrer">421</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0430" target="_blank" rel="noreferrer">430</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0615" target="_blank" rel="noreferrer">615</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0663" target="_blank" rel="noreferrer">663</a>.</p>',5),i=[s];function l(n,p,c,h,d,u){return o(),t("div",null,i)}const _=e(r,[["render",l]]);export{b as __pageData,_ as default};
