import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 22.1-24.1 (Effective until July 2, 2024) Internet Safety Advisory Council.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-24.1.md","filePath":"vacode/22.1-24.1.md"}'),n={name:"vacode/22.1-24.1.md"},i=a('<h1 id="§-22-1-24-1-effective-until-july-2-2024-internet-safety-advisory-council" tabindex="-1">§ 22.1-24.1 (Effective until July 2, 2024) Internet Safety Advisory Council. <a class="header-anchor" href="#§-22-1-24-1-effective-until-july-2-2024-internet-safety-advisory-council" aria-label="Permalink to &quot;§ 22.1-24.1 (Effective until July 2, 2024) Internet Safety Advisory Council.&quot;">​</a></h1><p>A. The Superintendent shall establish and appoint members to the Internet Safety Advisory Council (the Council) for the purpose of advancing the goal of safe use of media and technology by students and teachers in public elementary and secondary schools in the Commonwealth.</p><p>B. The membership of the Council shall include at least one (i) teacher; (ii) librarian; (iii) representative of a parent-teacher organization who is a parent of a school-age child; (iv) school administrator; (v) student; and (vi) individual with expertise in Internet safety and such other individuals as the Superintendent deems appropriate, provided that the membership of the Council shall not exceed 12.</p><p>C. The Council shall:</p><ol><li><p>Develop and recommend to the Board for adoption, after study and consideration of a variety of sources and perspectives, a model policy for local school boards in the Commonwealth that would enable such school boards to better support the Internet safety of all students and teachers in the local school division;</p></li><li><p>Develop and recommend to the Board for adoption model instructional practices for the safe use of media and technology by students and teachers in public elementary and secondary schools in the Commonwealth; and</p></li><li><p>Design and post on the Department&#39;s website a page with links to successful instructional practices, curricula, and other teacher resources used in school divisions within and outside of the Commonwealth for the safe use of media and technology by students and teachers.</p></li></ol><p>2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0776" target="_blank" rel="noreferrer">776</a>.</p>',6),s=[i];function r(l,c,d,h,p,u){return t(),o("div",null,s)}const v=e(n,[["render",r]]);export{m as __pageData,v as default};
