import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 22.1-254.01 Certain students required to attend summer school or after-school sessions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-254.01.md","filePath":"vacode/22.1-254.01.md"}'),i={name:"vacode/22.1-254.01.md"},o=a('<h1 id="§-22-1-254-01-certain-students-required-to-attend-summer-school-or-after-school-sessions" tabindex="-1">§ 22.1-254.01 Certain students required to attend summer school or after-school sessions. <a class="header-anchor" href="#§-22-1-254-01-certain-students-required-to-attend-summer-school-or-after-school-sessions" aria-label="Permalink to &quot;§ 22.1-254.01 Certain students required to attend summer school or after-school sessions.&quot;">​</a></h1><p>The division superintendent may seek immediate compliance with the compulsory school attendance law as set forth in § <a href="http://law.lis.virginia.gov/vacode/22.1-254/" target="_blank" rel="noreferrer">22.1-254</a> after a reasonable effort to seek the student&#39;s attendance in the summer school program or after-school session has failed, including direct notification of the parents of such student of the attendance requirement and failure of the parents to secure the student&#39;s attendance, when:</p><ol><li><p>A student is required to take a special program of prevention, intervention, or remediation in a public summer school program or to participate in another form of remediation as provided in subsection C of § <a href="http://law.lis.virginia.gov/vacode/22.1-253.13:1/" target="_blank" rel="noreferrer">22.1-253.13:1</a> and in accordance with clause (ii) of subsection A of § <a href="http://law.lis.virginia.gov/vacode/22.1-254/" target="_blank" rel="noreferrer">22.1-254</a>; and</p></li><li><p>The division superintendent determines that remediation of the student&#39;s poor academic performance, passage of the Standards of Learning Assessment in grades three through eight, or promotion is related directly to the student&#39;s attendance in the summer school program or participation in another form of remediation.</p></li></ol><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0163" target="_blank" rel="noreferrer">163</a>; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0466" target="_blank" rel="noreferrer">466</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0828" target="_blank" rel="noreferrer">828</a>; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0602" target="_blank" rel="noreferrer">602</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0627" target="_blank" rel="noreferrer">627</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0902" target="_blank" rel="noreferrer">902</a>; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0488" target="_blank" rel="noreferrer">488</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0552" target="_blank" rel="noreferrer">552</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0041" target="_blank" rel="noreferrer">41</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0834" target="_blank" rel="noreferrer">834</a>.</p>',4),n=[o];function s(l,c,h,d,f,g){return r(),t("div",null,n)}const m=e(i,[["render",s]]);export{u as __pageData,m as default};
