import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 22.1-265 Inducing children to absent themselves.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-265.md","filePath":"vacode/22.1-265.md"}'),o={name:"vacode/22.1-265.md"},i=n('<h1 id="§-22-1-265-inducing-children-to-absent-themselves" tabindex="-1">§ 22.1-265 Inducing children to absent themselves. <a class="header-anchor" href="#§-22-1-265-inducing-children-to-absent-themselves" aria-label="Permalink to &quot;§ 22.1-265 Inducing children to absent themselves.&quot;">​</a></h1><p>Any person who induces or attempts to induce any child to be absent unlawfully from school or who knowingly employs or harbors, while school is in session, any child absent unlawfully shall be guilty of a Class 3 misdemeanor and may be subject to the penalties provided by subdivision 5 a of subsection B of § <a href="http://law.lis.virginia.gov/vacode/16.1-278.5/" target="_blank" rel="noreferrer">16.1-278.5</a> or § <a href="http://law.lis.virginia.gov/vacode/18.2-371/" target="_blank" rel="noreferrer">18.2-371</a>. Upon a finding that a person knowingly and willfully violated the provisions of this section and that such person has been convicted previously of a violation of this section, such person shall be guilty of a Class 2 misdemeanor.</p><p>Code 1950, § 22-275.19; 1959, Ex. Sess., c. 72; 1976, c. 283; 1980, c. 559; 1990, c. 797; 1991, cc. 295, 534; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0891" target="_blank" rel="noreferrer">891</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0916" target="_blank" rel="noreferrer">916</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0964" target="_blank" rel="noreferrer">964</a>.</p>',3),r=[i];function s(l,c,h,d,f,p){return a(),t("div",null,r)}const u=e(o,[["render",s]]);export{g as __pageData,u as default};
