import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 22.1-271.1 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-271.1.md","filePath":"vacode/22.1-271.1.md"}'),i={name:"vacode/22.1-271.1.md"},r=a('<h1 id="§-22-1-271-1-definitions" tabindex="-1">§ 22.1-271.1 Definitions. <a class="header-anchor" href="#§-22-1-271-1-definitions" aria-label="Permalink to &quot;§ 22.1-271.1 Definitions.&quot;">​</a></h1><p>For the purpose of § <a href="http://law.lis.virginia.gov/vacode/22.1-271.2/" target="_blank" rel="noreferrer">22.1-271.2</a>:</p><p>&quot;Admit&quot; or &quot;admission&quot; means the official enrollment or reenrollment for attendance at any grade level, whether full-time or part-time, of any student by any school.</p><p>&quot;Admitting official&quot; means the school principal or his designated representative if a public school; if a nonpublic school or child-care center, the principal, headmaster or director of the school or center.</p><p>&quot;Documentary proof&quot; means written certification that a student has been immunized, such certificate to be on a form provided by the State Department of Health and signed by the licensed immunizing physician or an employee of the immunizing local health department.</p><p>&quot;Student&quot; means any person who seeks admission to a school, or for whom admission to a school is sought by a parent or guardian, and who will not have attained the age of 20 years by the start of the school term for which admission is sought.</p><p>&quot;Immunized&quot; or &quot;immunization&quot; means initial immunization and any boosters or reimmunizations required by § <a href="http://law.lis.virginia.gov/vacode/32.1-46/" target="_blank" rel="noreferrer">32.1-46</a>.</p><p>&quot;School&quot; means (i) any public school from kindergarten through grade 12 operated under the authority of any locality within the Commonwealth, (ii) any private or religious school that offers instruction at any level or grade from kindergarten through grade twelve, and (iii) any private or religious nursery school or preschool, or any private or religious child-care center required to be licensed by the Commonwealth.</p><p>1982, c. 510; 1983, c. 433; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0928" target="_blank" rel="noreferrer">928</a>.</p>',9),n=[r];function s(l,h,c,d,p,u){return o(),t("div",null,n)}const _=e(i,[["render",s]]);export{f as __pageData,_ as default};
