import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 22.1-271.4 Health requirements for home-instructed, exempted, and excused children.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-271.4.md","filePath":"vacode/22.1-271.4.md"}'),a={name:"vacode/22.1-271.4.md"},n=r('<h1 id="§-22-1-271-4-health-requirements-for-home-instructed-exempted-and-excused-children" tabindex="-1">§ 22.1-271.4 Health requirements for home-instructed, exempted, and excused children. <a class="header-anchor" href="#§-22-1-271-4-health-requirements-for-home-instructed-exempted-and-excused-children" aria-label="Permalink to &quot;§ 22.1-271.4 Health requirements for home-instructed, exempted, and excused children.&quot;">​</a></h1><p>In addition to compliance with the requirements of subsection B, D, or I of § <a href="http://law.lis.virginia.gov/vacode/22.1-254/" target="_blank" rel="noreferrer">22.1-254</a> or § <a href="http://law.lis.virginia.gov/vacode/22.1-254.1/" target="_blank" rel="noreferrer">22.1-254.1</a>, any parent, guardian or other person having control or charge of a child being home instructed, exempted or excused from school attendance shall comply with the immunization requirements provided in § <a href="http://law.lis.virginia.gov/vacode/32.1-46/" target="_blank" rel="noreferrer">32.1-46</a> in the same manner and to the same extent as if the child has been enrolled in and is attending school.</p><p>Upon request by the division superintendent, the parent shall submit to such division superintendent documentary proof of immunization in compliance with § <a href="http://law.lis.virginia.gov/vacode/32.1-46/" target="_blank" rel="noreferrer">32.1-46</a>.</p><p>No proof of immunization shall be required of any child upon submission of (i) an affidavit to the division superintendent stating that the administration of immunizing agents conflicts with the parent&#39;s or guardian&#39;s religious tenets or practices or (ii) a written certification from a licensed physician, licensed nurse practitioner, or local health department that one or more of the required immunizations may be detrimental to the child&#39;s health, indicating the specific nature of the medical condition or circumstance that contraindicates immunization.</p><p>1993, c. 659; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0488" target="_blank" rel="noreferrer">488</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0552" target="_blank" rel="noreferrer">552</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0605" target="_blank" rel="noreferrer">605</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0125" target="_blank" rel="noreferrer">125</a>.</p>',5),o=[n];function s(c,l,d,h,m,p){return t(),i("div",null,o)}const g=e(a,[["render",s]]);export{u as __pageData,g as default};
