import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 22.1-276.01 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-276.01.md","filePath":"vacode/22.1-276.01.md"}'),a={name:"vacode/22.1-276.01.md"},r=i('<h1 id="§-22-1-276-01-definitions" tabindex="-1">§ 22.1-276.01 Definitions. <a class="header-anchor" href="#§-22-1-276-01-definitions" aria-label="Permalink to &quot;§ 22.1-276.01 Definitions.&quot;">​</a></h1><p>A. For the purposes of this article, unless the context requires a different meaning:</p><p>&quot;Alternative education program&quot; includes night school, adult education, or any other education program designed to offer instruction to students for whom the regular program of instruction may be inappropriate.</p><p>&quot;Bullying&quot; means any aggressive and unwanted behavior that is intended to harm, intimidate, or humiliate the victim; involves a real or perceived power imbalance between the aggressor or aggressors and victim; and is repeated over time or causes severe emotional trauma. &quot;Bullying&quot; includes cyber bullying. &quot;Bullying&quot; does not include ordinary teasing, horseplay, argument, or peer conflict.</p><p>&quot;Disruptive behavior&quot; means a violation of school board regulations governing student conduct that interrupts or obstructs the learning environment.</p><p>&quot;Dress or grooming code&quot; means any practice, policy, or portion of a code of student conduct adopted by a school board that governs or restricts the attire, appearance, or grooming, including hairstyle, of any enrolled student.</p><p>&quot;Exclusion&quot; means a Virginia school board&#39;s denial of school admission to a student who has been expelled or has been placed on a long-term suspension of more than 30 calendar days by another school board or a private school, either in Virginia or another state, or for whom admission has been withdrawn by a private school in Virginia or another state.</p><p>&quot;Expulsion&quot; means any disciplinary action imposed by a school board or a committee thereof, as provided in school board policy, whereby a student is not permitted to attend school within the school division and is ineligible for readmission for 365 calendar days after the date of the expulsion.</p><p>&quot;Long-term suspension&quot; means any disciplinary action whereby a student is not permitted to attend school for 11 to 45 school days.</p><p>&quot;Short-term suspension&quot; means any disciplinary action whereby a student is not permitted to attend school for a period not to exceed 10 school days.</p><p>B. For the purposes of §§ <a href="/vacode/22.1-277.04/">22.1-277.04</a>, <a href="/vacode/22.1-277.05/">22.1-277.05</a>, <a href="/vacode/22.1-277.2/">22.1-277.2</a>, and <a href="/vacode/22.1-277.2:1/">22.1-277.2:1</a>, &quot;superintendent&#39;s designee&quot; means a (i) trained hearing officer or (ii) professional employee within the administrative offices of the school division who reports directly to the division superintendent and who is not a school-based instructional or administrative employee.</p><p>2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0688" target="_blank" rel="noreferrer">688</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0820" target="_blank" rel="noreferrer">820</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0575" target="_blank" rel="noreferrer">575</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0491" target="_blank" rel="noreferrer">491</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0678" target="_blank" rel="noreferrer">678</a>.</p>',12),n=[r];function s(d,l,c,h,p,u){return o(),t("div",null,n)}const m=e(a,[["render",s]]);export{f as __pageData,m as default};
