import{_ as e,o as t,c as n,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 22.1-279.1:1 The use of seclusion and restraint in public schools; Board of Education regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-279.1:1.md","filePath":"vacode/22.1-279.1:1.md"}'),o={name:"vacode/22.1-279.1:1.md"},a=i('<h1 id="§-22-1-279-1-1-the-use-of-seclusion-and-restraint-in-public-schools-board-of-education-regulations" tabindex="-1">§ 22.1-279.1:1 The use of seclusion and restraint in public schools; Board of Education regulations. <a class="header-anchor" href="#§-22-1-279-1-1-the-use-of-seclusion-and-restraint-in-public-schools-board-of-education-regulations" aria-label="Permalink to &quot;§ 22.1-279.1:1 The use of seclusion and restraint in public schools; Board of Education regulations.&quot;">​</a></h1><p>The Board shall adopt regulations on the use of seclusion and restraint in public elementary and secondary schools in the Commonwealth that (i) are consistent with its Guidelines for the Development of Policies and Procedures for Managing Student Behavior in Emergency Situations and the Fifteen Principles contained in the U.S. Department of Education&#39;s Restraint and Seclusion: Resource Document; (ii) include definitions, criteria for use, restrictions for use, training requirements, notification requirements, reporting requirements, and follow-up requirements; and (iii) address distinctions, including distinctions in emotional and physical development, between (a) the general student population and the special education student population and (b) elementary school students and secondary school students. The Board shall specifically (1) identify and prohibit the use of any method of restraint or seclusion that it determines poses a significant danger to the student and (2) establish safety standards for seclusion.</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0142" target="_blank" rel="noreferrer">142</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0147" target="_blank" rel="noreferrer">147</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0591" target="_blank" rel="noreferrer">591</a>.</p>',3),s=[a];function r(c,l,d,u,h,f){return t(),n("div",null,s)}const g=e(o,[["render",r]]);export{_ as __pageData,g as default};
