import{_ as e,o,c as i,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 22.1-280.2:1 Employment of school security officers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-280.2:1.md","filePath":"vacode/22.1-280.2:1.md"}'),a={name:"vacode/22.1-280.2:1.md"},t=r('<h1 id="§-22-1-280-2-1-employment-of-school-security-officers" tabindex="-1">§ 22.1-280.2:1 Employment of school security officers. <a class="header-anchor" href="#§-22-1-280-2-1-employment-of-school-security-officers" aria-label="Permalink to &quot;§ 22.1-280.2:1 Employment of school security officers.&quot;">​</a></h1><p>Local school boards and private or religious schools may employ school security officers, as defined in § <a href="/vacode/9.1-101/">9.1-101</a>, for the purposes set forth therein. Such school security officer may carry a firearm in the performance of his duties if (i) within 10 years immediately prior to being hired by the local school board or private or religious school he (a) was an active law-enforcement officer as defined in § <a href="/vacode/9.1-101/">9.1-101</a> in the Commonwealth or (b) was employed by a law-enforcement agency of the United States or any state or political subdivision thereof and his duties were substantially similar to those of a law-enforcement officer as defined in § <a href="/vacode/9.1-101/">9.1-101</a>; (ii) he retired or resigned from his position as a law-enforcement officer in good standing; (iii) he meets the training and qualifications described in subsection C of § <a href="/vacode/18.2-308.016/">18.2-308.016</a>; (iv) he has provided proof of completion of a training course that includes training in active shooter emergency response, emergency evacuation procedure, and threat assessment to the Department of Criminal Justice Services pursuant to subdivision 42 of § <a href="/vacode/9.1-102/">9.1-102</a>, provided that if he received such training from a local law-enforcement agency he received the training in the locality in which he is employed; (v) the local school board or private or religious school solicits input from the chief law-enforcement officer of the locality regarding the qualifications of the school security officer and receives verification from such chief law-enforcement officer that the school security officer is not prohibited by state or federal law from possessing, purchasing, or transporting a firearm; and (vi) the local school board or private or religious school grants him the authority to carry a firearm in the performance of his duties.</p><p>2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0836" target="_blank" rel="noreferrer">836</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0311" target="_blank" rel="noreferrer">311</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0120" target="_blank" rel="noreferrer">120</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0493" target="_blank" rel="noreferrer">493</a>.</p>',3),n=[t];function s(c,f,l,h,d,p){return o(),i("div",null,n)}const u=e(a,[["render",s]]);export{g as __pageData,u as default};
