import{_ as e,o,c as a,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 22.1-280.2:3 School boards; safety and security personnel.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-280.2:3.md","filePath":"vacode/22.1-280.2:3.md"}'),n={name:"vacode/22.1-280.2:3.md"},i=r('<h1 id="§-22-1-280-2-3-school-boards-safety-and-security-personnel" tabindex="-1">§ 22.1-280.2:3 School boards; safety and security personnel. <a class="header-anchor" href="#§-22-1-280-2-3-school-boards-safety-and-security-personnel" aria-label="Permalink to &quot;§ 22.1-280.2:3 School boards; safety and security personnel.&quot;">​</a></h1><p>A. The school board in each school division in which the local law-enforcement agency employs school resource officers, as defined in § <a href="/vacode/9.1-101/">9.1-101</a>, shall enter into a memorandum of understanding with such local law-enforcement agency that sets forth the powers and duties of such school resource officers. The provisions of such memorandum of understanding shall be based on the model memorandum of understanding developed by the Virginia Center for School and Campus Safety pursuant to subdivision A 12 of § <a href="/vacode/9.1-184/">9.1-184</a>, which may be modified by the parties in accordance with their particular needs. Each such school board and local law-enforcement agency shall review and amend or affirm such memorandum at least once every two years or at any time upon the request of either party. Each school board shall ensure the current division memorandum of understanding is conspicuously published on the division website and provide notice and opportunity for public input during each memorandum of understanding review period.</p><p>B. The chief local law-enforcement officer for any local school division in which a public elementary or secondary school does not employ a school resource officer, as defined in § <a href="/vacode/9.1-101/">9.1-101</a>, shall designate a law-enforcement officer to receive, either in-person or online, the training set forth in subsection E of § <a href="/vacode/22.1-279.8/">22.1-279.8</a>. Such officer shall serve as the law-enforcement liaison for the school administrator described in subsection E of § <a href="/vacode/22.1-279.8/">22.1-279.8</a> in each public elementary or secondary school that does not employ a school resource officer.</p><p>2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0455" target="_blank" rel="noreferrer">455</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0052" target="_blank" rel="noreferrer">52</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0171" target="_blank" rel="noreferrer">171</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0769" target="_blank" rel="noreferrer">769</a>.</p>',4),t=[i];function s(c,l,h,d,f,u){return o(),a("div",null,t)}const g=e(n,[["render",s]]);export{m as __pageData,g as default};
