import{_ as e,o as t,c as o,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 22.1-287.04 Uniformed services-connected students.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-287.04.md","filePath":"vacode/22.1-287.04.md"}'),r={name:"vacode/22.1-287.04.md"},s=n('<h1 id="§-22-1-287-04-uniformed-services-connected-students" tabindex="-1">§ 22.1-287.04 Uniformed services-connected students. <a class="header-anchor" href="#§-22-1-287-04-uniformed-services-connected-students" aria-label="Permalink to &quot;§ 22.1-287.04 Uniformed services-connected students.&quot;">​</a></h1><p>A. For purposes of this section, a &quot;uniformed services-connected student&quot; means a student enrolled in a public school whose parent is serving in either (i) the active component of the U.S. Army, Navy, Air Force, Marine Corps, Coast Guard, or National Guard, the Commissioned Corps of the National Oceanic and Atmospheric Administration, or the Commissioned Corps of the U.S. Public Health Services or (ii) the reserve component of the U.S. Army, Navy, Air Force, Marine Corps, Coast Guard, or National Guard.</p><p>B. The Department of Education shall establish a process for the identification of newly enrolled uniformed services-connected students by local school divisions. Local school divisions shall identify newly enrolled uniformed services-connected students in accordance with such process.</p><p>C. Nonidentifiable, aggregate data collected from the identification of uniformed services-connected students shall be made available to local, state, and federal entities for the purposes of becoming eligible for nongeneral fund sources and receiving services to meet the needs of uniformed services-connected students residing in the Commonwealth.</p><p>D. Data collected from the identification of uniformed services-connected students shall not be a public record as defined in § <a href="http://law.lis.virginia.gov/vacode/2.2-3701/" target="_blank" rel="noreferrer">2.2-3701</a>. No person shall disclose such data except as permitted under the provisions of the federal Family Educational Rights and Privacy Act (20 U.S.C. § 1232g) and related regulations. No such data shall be used for the purposes of student achievement, the Standards of Accreditation, student-growth indicators, the school performance report card, or any other school rating system.</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0582" target="_blank" rel="noreferrer">582</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0583" target="_blank" rel="noreferrer">583</a>.</p>',6),i=[s];function a(c,d,l,h,f,u){return t(),o("div",null,i)}const v=e(r,[["render",a]]);export{m as __pageData,v as default};
