import{_ as s,o as i,c as t,z as e,a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 22.1-289.010 Application fees; regulations and schedules; use of fees; certain facilities, centers and agencies exempt.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-289.010.md","filePath":"vacode/22.1-289.010.md"}'),n={name:"vacode/22.1-289.010.md"},r=e("h1",{id:"§-22-1-289-010-application-fees-regulations-and-schedules-use-of-fees-certain-facilities-centers-and-agencies-exempt",tabindex:"-1"},[a("§ 22.1-289.010 Application fees; regulations and schedules; use of fees; certain facilities, centers and agencies exempt. "),e("a",{class:"header-anchor",href:"#§-22-1-289-010-application-fees-regulations-and-schedules-use-of-fees-certain-facilities-centers-and-agencies-exempt","aria-label":'Permalink to "§ 22.1-289.010 Application fees; regulations and schedules; use of fees; certain facilities, centers and agencies exempt."'},"​")],-1),o=e("p",null,"The Board is authorized to adopt regulations and schedules for fees to be charged for processing applications for licenses to operate child day programs and family day systems. Such schedules shall specify minimum and maximum fees and, where appropriate, gradations based on the capacity of such facilities, centers, and agencies. Fees shall be used for the development and delivery of training for operators and staff of child day programs and family day systems. Fees shall be expended for this purpose within two fiscal years following the fiscal year in which they are collected. These fees shall not be applicable to facilities, centers, or agencies operated by federal entities.",-1),l=e("p",null,"The Board shall develop training programs for operators and staffs of licensed child day programs. Such programs shall include formal and informal training offered by institutions of higher education, state and national associations representing child care professionals, local and regional early childhood educational organizations, state agencies and other trainers designated by the Board, and licensed child care providers. Training provided to operators and staffs of licensed child day programs shall include training and information regarding shaken baby syndrome, its effects, and resources for help and support for caretakers. To the maximum extent possible, the Board shall ensure that all provider interests are represented and that no single approach to training shall be given preference.",-1),c=e("p",null,[a("2020, cc. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0860",target:"_blank",rel:"noreferrer"},"860"),a(", "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0861",target:"_blank",rel:"noreferrer"},"861"),a(".")],-1),d=[r,o,l,c];function f(p,h,g,u,m,_){return i(),t("div",null,d)}const v=s(n,[["render",f]]);export{b as __pageData,v as default};
