import{_ as e,o as a,c as o,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 22.1-289.024 (Effective January 1 2022) Appeal from refusal, denial of renewal, or revocation of license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-289.024.md","filePath":"vacode/22.1-289.024.md"}'),t={name:"vacode/22.1-289.024.md"},n=r('<h1 id="§-22-1-289-024-effective-january-1-2022-appeal-from-refusal-denial-of-renewal-or-revocation-of-license" tabindex="-1">§ 22.1-289.024 (Effective January 1 2022) Appeal from refusal, denial of renewal, or revocation of license. <a class="header-anchor" href="#§-22-1-289-024-effective-january-1-2022-appeal-from-refusal-denial-of-renewal-or-revocation-of-license" aria-label="Permalink to &quot;§ 22.1-289.024 (Effective January 1 2022) Appeal from refusal, denial of renewal, or revocation of license.&quot;">​</a></h1><p>A. Whenever the Superintendent refuses to issue a license or to renew a license or revokes a license for a child day program or family day system operated by an agency of the Commonwealth, the provisions of § <a href="/vacode/22.1-289.02/">22.1-289.02</a>5 shall apply. Whenever the Superintendent refuses to issue a license or to renew a license or revokes a license for any child day program or family day system other than a child day program or family day system operated by an agency of the Commonwealth, the provisions of the Administrative Process Act (§ <a href="/vacode/2.2-4000/">2.2-4000</a> et seq.) shall apply, except that all appeals from notice of the Superintendent&#39;s intent to refuse to issue or renew, or revoke a license shall be received in writing from the child day program or family day system operator within 15 days of the date of receipt of the notice. Judicial review of a final review agency decision shall be in accordance with the provisions of the Administrative Process Act. No stay may be granted upon appeal to the Supreme Court or the Court of Appeals.</p><p>B. In every appeal to a court of record, the Superintendent shall be named defendant.</p><p>C. An appeal, taken as provided in this section, shall operate to stay any criminal prosecution for operation without a license.</p><p>D. When issuance or renewal of a license for a child day program or family day system has been refused by the Superintendent, the applicant shall not thereafter for a period of six months apply again for such license unless the Superintendent in his sole discretion believes that there has been such a change in the conditions on account of which he refused the prior application as to justify considering the new application. When an appeal is taken by the applicant pursuant to subsection A, the six-month period shall be extended until a final decision has been rendered on appeal.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0861" target="_blank" rel="noreferrer">861</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0489" target="_blank" rel="noreferrer">489</a>.</p>',6),i=[n];function s(l,c,p,f,h,d){return a(),o("div",null,i)}const y=e(t,[["render",s]]);export{m as __pageData,y as default};
