import{_ as e,o as a,c as n,O as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 22.1-289.038 Child day programs and family day systems; criminal conviction and waiver.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-289.038.md","filePath":"vacode/22.1-289.038.md"}'),i={name:"vacode/22.1-289.038.md"},r=t('<h1 id="§-22-1-289-038-child-day-programs-and-family-day-systems-criminal-conviction-and-waiver" tabindex="-1">§ 22.1-289.038 Child day programs and family day systems; criminal conviction and waiver. <a class="header-anchor" href="#§-22-1-289-038-child-day-programs-and-family-day-systems-criminal-conviction-and-waiver" aria-label="Permalink to &quot;§ 22.1-289.038 Child day programs and family day systems; criminal conviction and waiver.&quot;">​</a></h1><p>A. Any person who seeks to operate, volunteer, or work at a child day program or family day system and who is disqualified because of a criminal conviction or a criminal conviction in the background check of any other adult living in a family day home regulated by the Department, pursuant to § <a href="/vacode/22.1-289.035/">22.1-289.035</a>, <a href="/vacode/22.1-289.036/">22.1-289.036</a>, or <a href="/vacode/22.1-289.039/">22.1-289.039</a>, may apply in writing for a waiver from the Superintendent. The Superintendent may grant a waiver if the Superintendent determines that (i) the person is of good moral character and reputation and (ii) the waiver would not adversely affect the safety and well-being of children in the person&#39;s care. The Superintendent shall not grant a waiver to any person who has been convicted of any barrier crime as defined in § <a href="/vacode/19.2-392.02/">19.2-392.02</a>. However, the Superintendent may grant a waiver to a family day home licensed or registered by the Department if any other adult living in the home of the applicant or provider has been convicted of not more than one misdemeanor offense under § <a href="/vacode/18.2-57/">18.2-57</a> or <a href="/vacode/18.2-57.2/">18.2-57.2</a>, or any substantially similar offense under the laws of another jurisdiction, provided that (a) five years have elapsed following the conviction and (b) the Department has conducted a home study that includes, but is not limited to, (1) an assessment of the safety of children placed in the home and (2) a determination that the offender is now a person of good moral character and reputation. The waiver shall not be granted if the adult living in the home is an assistant or substitute provider or if such adult has been convicted of a misdemeanor offense under both §§ <a href="/vacode/18.2-57/">18.2-57</a> and <a href="/vacode/18.2-57.2/">18.2-57.2</a>, or any substantially similar offense under the laws of another jurisdiction. Any waiver granted under this section shall be available for inspection by the public. The child day program or family day system shall notify in writing every parent and guardian of the children in its care of any waiver granted for its operators, employees, or volunteers.</p><p>B. The Board shall adopt regulations to implement the provisions of this section.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0861" target="_blank" rel="noreferrer">861</a>.</p>',4),o=[r];function d(s,l,c,h,f,m){return a(),n("div",null,o)}const v=e(i,[["render",d]]);export{y as __pageData,v as default};
