import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 22.1-289.039 Records check by unlicensed child day center; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-289.039.md","filePath":"vacode/22.1-289.039.md"}'),n={name:"vacode/22.1-289.039.md"},r=o('<h1 id="§-22-1-289-039-records-check-by-unlicensed-child-day-center-penalty" tabindex="-1">§ 22.1-289.039 Records check by unlicensed child day center; penalty. <a class="header-anchor" href="#§-22-1-289-039-records-check-by-unlicensed-child-day-center-penalty" aria-label="Permalink to &quot;§ 22.1-289.039 Records check by unlicensed child day center; penalty.&quot;">​</a></h1><p>Any child day center that is exempt from licensure pursuant to § <a href="/vacode/22.1-289.031/">22.1-289.031</a> shall require all applicants for employment, employees, applicants to serve as volunteers, and volunteers and any other person who is expected to be alone with one or more children enrolled in the child day center to obtain a background check in accordance with § <a href="/vacode/22.1-289.035/">22.1-289.035</a>. A child day center that is exempt from licensure pursuant to § <a href="/vacode/22.1-289.031/">22.1-289.031</a> shall refuse employment or service to any person who (i) has been convicted of any barrier crime as defined in § <a href="/vacode/19.2-392.02/">19.2-392.02</a> or (ii) is the subject of a founded complaint of child abuse or neglect within or outside the Commonwealth. The foregoing provisions shall not apply to a parent or guardian who may be left alone with his own child. For purposes of this section, convictions shall include prior adult convictions and juvenile convictions or adjudications of delinquency based on a crime that would have been a felony if committed by an adult within or outside the Commonwealth. Further dissemination of the information provided to the facility is prohibited, except as otherwise provided in subsection J of § <a href="/vacode/22.1-289.035/">22.1-289.035</a>.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0861" target="_blank" rel="noreferrer">861</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0251" target="_blank" rel="noreferrer">251</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0510" target="_blank" rel="noreferrer">510</a>.</p>',3),i=[r];function c(l,s,d,h,p,f){return a(),t("div",null,i)}const m=e(n,[["render",c]]);export{_ as __pageData,m as default};
