import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 22.1-289.040 Child day centers and family day homes receiving federal, state, or local child care funds; eligibility requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-289.040.md","filePath":"vacode/22.1-289.040.md"}'),r={name:"vacode/22.1-289.040.md"},n=i('<h1 id="§-22-1-289-040-child-day-centers-and-family-day-homes-receiving-federal-state-or-local-child-care-funds-eligibility-requirements" tabindex="-1">§ 22.1-289.040 Child day centers and family day homes receiving federal, state, or local child care funds; eligibility requirements. <a class="header-anchor" href="#§-22-1-289-040-child-day-centers-and-family-day-homes-receiving-federal-state-or-local-child-care-funds-eligibility-requirements" aria-label="Permalink to &quot;§ 22.1-289.040 Child day centers and family day homes receiving federal, state, or local child care funds; eligibility requirements.&quot;">​</a></h1><p>A. Whenever any child day center or family day home that has not met the requirements of §§ <a href="/vacode/22.1-289.035/">22.1-289.035</a>, <a href="/vacode/22.1-289.036/">22.1-289.036</a>, and <a href="/vacode/22.1-289.039/">22.1-289.039</a> applies to enter into a contract with the Department or its agents or designees to provide child care services to clients of the Department or its agents or designees, the Department or its agents or designees shall require a background check, at the time of application to enter into a contract and every five years thereafter, of (i) the applicant; any agents involved in the day-to-day operation; all agents who are alone with, in control of, or supervising one or more of the children; and any other adult living in a family day home pursuant to § <a href="/vacode/22.1-289.036/">22.1-289.036;</a> and (ii) all applicants for employment, employees, applicants to serve as volunteers, and volunteers pursuant to § <a href="/vacode/22.1-289.035/">22.1-289.035</a>. The child day center or family day home shall not be permitted to enter into a contract with the Department or its agents or designees for child care services when an applicant; any employee; a prospective employee; a volunteer, an agent involved in the day-to-day operation; an agent alone with, in control of, or supervising one or more children; or any other adult living in a family day home (i) has been convicted of any barrier crime as defined in § <a href="/vacode/19.2-392.02/">19.2-392.02</a> or (ii) is the subject of a founded complaint of child abuse or neglect within or outside the Commonwealth. Further dissemination of the information provided to the facility, beyond dissemination to the Department or its agents or designees is prohibited.</p><p>B. Every child day center or family day home that enters into a contract with the Department or its agents or designees to provide child care services to clients of the Department or its agents or designees that is funded, in whole or in part, by the Child Care and Development Block Grant, shall comply with all requirements established by federal law and regulations.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0861" target="_blank" rel="noreferrer">861</a>.</p>',4),o=[n];function l(s,d,c,h,f,m){return a(),t("div",null,o)}const g=e(r,[["render",l]]);export{y as __pageData,g as default};
