import{_ as o,o as i,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 22.1-289.042 Establishment of toll-free telephone line for complaints; investigation on receipt of complaints.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-289.042.md","filePath":"vacode/22.1-289.042.md"}'),n={name:"vacode/22.1-289.042.md"},s=e("h1",{id:"§-22-1-289-042-establishment-of-toll-free-telephone-line-for-complaints-investigation-on-receipt-of-complaints",tabindex:"-1"},[t("§ 22.1-289.042 Establishment of toll-free telephone line for complaints; investigation on receipt of complaints. "),e("a",{class:"header-anchor",href:"#§-22-1-289-042-establishment-of-toll-free-telephone-line-for-complaints-investigation-on-receipt-of-complaints","aria-label":'Permalink to "§ 22.1-289.042 Establishment of toll-free telephone line for complaints; investigation on receipt of complaints."'},"​")],-1),r=e("p",null,"With such funds as are appropriated for this purpose, the Superintendent shall establish a toll-free telephone line to respond to complaints regarding operations of child day programs or family day systems. Upon receipt of a complaint concerning the operation of a child day program or family day system, regardless of whether the program is subject to licensure, the Superintendent shall, for good cause shown, cause an investigation to be made, including on-site visits as he deems necessary, of the activities, services, records, and facilities. The child day program or family day system shall afford the Superintendent reasonable opportunity to inspect all of the operator's activities, services, records, and facilities and to interview its agents and employees and any child within its control. Whenever a child day program or family day system subject to inspection under this section is determined by the Superintendent to be in noncompliance with the provisions of this chapter or with regulations adopted pursuant to this chapter, the Superintendent shall give reasonable notice to the child day program or family day system of the nature of its noncompliance and may thereafter take appropriate action as provided by law, including a suit to enjoin the operation of the child day program or family day system.",-1),l=e("p",null,[t("2020, cc. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0860",target:"_blank",rel:"noreferrer"},"860"),t(", "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0861",target:"_blank",rel:"noreferrer"},"861"),t(".")],-1),c=[s,r,l];function p(d,h,f,m,g,u){return i(),a("div",null,c)}const _=o(n,[["render",p]]);export{v as __pageData,_ as default};
