import{_ as e,o as a,c as o,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 22.1-29.1 Public hearing before appointment of school board members.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-29.1.md","filePath":"vacode/22.1-29.1.md"}'),t={name:"vacode/22.1-29.1.md"},i=r('<h1 id="§-22-1-29-1-public-hearing-before-appointment-of-school-board-members" tabindex="-1">§ 22.1-29.1 Public hearing before appointment of school board members. <a class="header-anchor" href="#§-22-1-29-1-public-hearing-before-appointment-of-school-board-members" aria-label="Permalink to &quot;§ 22.1-29.1 Public hearing before appointment of school board members.&quot;">​</a></h1><p>At least seven days prior to the appointment of any school board member pursuant to the provisions of this chapter, of §§ <a href="http://law.lis.virginia.gov/vacode/15.2-410/" target="_blank" rel="noreferrer">15.2-410</a>, <a href="http://law.lis.virginia.gov/vacode/15.2-531/" target="_blank" rel="noreferrer">15.2-531</a>, <a href="http://law.lis.virginia.gov/vacode/15.2-627/" target="_blank" rel="noreferrer">15.2-627</a> or § <a href="http://law.lis.virginia.gov/vacode/15.2-837/" target="_blank" rel="noreferrer">15.2-837</a>, or of any municipal charter, the appointing authority shall hold one or more public hearings to receive the views of citizens within the school division. The appointing authority shall cause public notice to be given at least ten days prior to any hearing by publication in a newspaper having a general circulation within the school division. No nominee or applicant whose name has not been considered at a public hearing shall be appointed as a school board member.</p><p>1985, c. 423; 1987, c. 430.</p>',3),n=[i];function s(l,c,h,p,b,d){return a(),o("div",null,n)}const f=e(t,[["render",s]]);export{_ as __pageData,f as default};
