import{_ as e,o,c as r,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 22.1-292.2 Suspension or revocation of license for procuring, selling, or administering anabolic steroids.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-292.2.md","filePath":"vacode/22.1-292.2.md"}'),t={name:"vacode/22.1-292.2.md"},n=i('<h1 id="§-22-1-292-2-suspension-or-revocation-of-license-for-procuring-selling-or-administering-anabolic-steroids" tabindex="-1">§ 22.1-292.2 Suspension or revocation of license for procuring, selling, or administering anabolic steroids. <a class="header-anchor" href="#§-22-1-292-2-suspension-or-revocation-of-license-for-procuring-selling-or-administering-anabolic-steroids" aria-label="Permalink to &quot;§ 22.1-292.2 Suspension or revocation of license for procuring, selling, or administering anabolic steroids.&quot;">​</a></h1><p>A. The Board of Education shall suspend or revoke the administrative or teaching license it has issued to any person who knowingly and willfully with the intent to compromise the outcome of an athletic competition procures, sells, or administers anabolic steroids or causes such drugs to be procured, sold, or administered to a student who is a member of a school athletic team, or fails to report the use of such drugs by a student to the school principal and division superintendent as required by § <a href="http://law.lis.virginia.gov/vacode/22.1-279.3:1/" target="_blank" rel="noreferrer">22.1-279.3:1</a>. Any person whose administrative or teaching license is suspended or revoked by the Board pursuant to this section shall be ineligible for three school years for employment in the public schools of the Commonwealth.</p><p>B. Any suspension or revocation imposed in accordance with this section shall be rendered pursuant to Board regulations promulgated pursuant to the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.) and § <a href="http://law.lis.virginia.gov/vacode/22.1-298.1/" target="_blank" rel="noreferrer">22.1-298.1</a>, governing the licensure of teachers.</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0481" target="_blank" rel="noreferrer">481</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0027" target="_blank" rel="noreferrer">27</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0349" target="_blank" rel="noreferrer">349</a>.</p>',4),a=[n];function s(l,c,d,h,p,g){return o(),r("div",null,a)}const _=e(t,[["render",s]]);export{f as __pageData,_ as default};
