import{_ as e,o as r,c as a,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 22.1-298.2 Regulations governing education preparation programs.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-298.2.md","filePath":"vacode/22.1-298.2.md"}'),t={name:"vacode/22.1-298.2.md"},n=i('<h1 id="§-22-1-298-2-regulations-governing-education-preparation-programs" tabindex="-1">§ 22.1-298.2 Regulations governing education preparation programs. <a class="header-anchor" href="#§-22-1-298-2-regulations-governing-education-preparation-programs" aria-label="Permalink to &quot;§ 22.1-298.2 Regulations governing education preparation programs.&quot;">​</a></h1><p>A. As used in this section, &quot;education preparation program&quot; includes four-year bachelor&#39;s degree programs in teacher education.</p><p>B. Education preparation programs shall meet the requirements for accreditation and program approval as prescribed by the Board of Education in its regulations.</p><p>C. The Board of Education regulations shall provide for education preparation programs offered by institutions of higher education, Virginia public school divisions, and certified providers for alternate routes to licensure.</p><p>D. The Board may prescribe requirements for admission to Virginia&#39;s approved education preparation programs in its regulations.</p><p>E. The Board shall establish accountability measures for approved education programs. Data shall be submitted to the Board on not less than a biennial basis.</p><p>F. The Board shall review and amend its regulations governing general education teacher preparation programs for kindergarten through twelfth grade to ensure graduates are required to demonstrate proficiency in (i) differentiating instruction for students depending on their needs; (ii) understanding the role of general education teachers on the individualized education program team; (iii) implementing effective models of collaborative instruction, including co-teaching; and (iv) understanding the goals and benefits of inclusive education for all students.</p><p>G. The Board shall review and amend its regulations governing administrator preparation programs to ensure graduates are required to demonstrate comprehension of (i) key special education laws and regulations, (ii) individualized education program development, (iii) the roles and responsibilities of special education teachers, and (iv) appropriate behavior management practices.</p><p>2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0027" target="_blank" rel="noreferrer">27</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0349" target="_blank" rel="noreferrer">349</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0518" target="_blank" rel="noreferrer">518</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0748" target="_blank" rel="noreferrer">748</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0749" target="_blank" rel="noreferrer">749</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0063" target="_blank" rel="noreferrer">63</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0407" target="_blank" rel="noreferrer">407</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0451" target="_blank" rel="noreferrer">451</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0452" target="_blank" rel="noreferrer">452</a>.</p>',9),o=[n];function s(l,g,p,d,c,u){return r(),a("div",null,o)}const v=e(t,[["render",s]]);export{f as __pageData,v as default};
