import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 22.1-298.6 Mental health awareness training.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-298.6.md","filePath":"vacode/22.1-298.6.md"}'),n={name:"vacode/22.1-298.6.md"},i=r('<h1 id="§-22-1-298-6-mental-health-awareness-training" tabindex="-1">§ 22.1-298.6 Mental health awareness training. <a class="header-anchor" href="#§-22-1-298-6-mental-health-awareness-training" aria-label="Permalink to &quot;§ 22.1-298.6 Mental health awareness training.&quot;">​</a></h1><p>A. Each school board shall adopt and implement policies that require each teacher and other relevant personnel, as determined by the school board, employed on a full-time basis, to complete a mental health awareness training or similar program at least once.</p><p>B. Each school board shall provide required personnel the training required by subsection A and may contract with the Department of Behavioral Health and Developmental Services, a community services board, a behavioral health authority, a nonprofit organization, or other certified trainer as defined in § <a href="/vacode/37.2-312.2/">37.2-312.2</a> to provide such training. Such training may be provided via an online module.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0471" target="_blank" rel="noreferrer">471</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0472" target="_blank" rel="noreferrer">472</a>.</p>',4),o=[i];function l(s,h,c,d,p,_){return a(),t("div",null,o)}const f=e(n,[["render",l]]);export{g as __pageData,f as default};
