import{_ as e,o as r,c as a,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 22.1-299.7 Microcredential program; certain STEM subjects.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-299.7.md","filePath":"vacode/22.1-299.7.md"}'),t={name:"vacode/22.1-299.7.md"},n=o('<h1 id="§-22-1-299-7-microcredential-program-certain-stem-subjects" tabindex="-1">§ 22.1-299.7 Microcredential program; certain STEM subjects. <a class="header-anchor" href="#§-22-1-299-7-microcredential-program-certain-stem-subjects" aria-label="Permalink to &quot;§ 22.1-299.7 Microcredential program; certain STEM subjects.&quot;">​</a></h1><p>A. The Department of Education may establish a microcredential program for the purpose of permitting any public elementary or secondary school teacher who holds a renewable or provisional license or any individual who participates in any alternate route to licensure program to complete additional coursework and earn microcredentials in science, technology, engineering, and mathematics (STEM) endorsement areas, including computer science, for which there is a high need for additional qualified teachers.</p><p>B. The Department of Education shall direct the Advisory Board on Teacher Education and Licensure to convene a workgroup including pertinent education stakeholders to determine how any microcredential awarded pursuant to any microcredential program established pursuant to subsection A will be used to award add-on endorsements and certifications for teachers in STEM endorsement areas, including computer science, for which there is a high need for additional qualified teachers.</p><p>C. Any course offered through any microcredential program established pursuant to subsection A shall be offered in-person or in a blended format of in-person and online instruction.</p><p>D. Any teacher who holds a renewable license and who participates, through any microcredential program offered pursuant to subsection A, in courses that do not contribute to an endorsement is eligible for professional development points toward renewal of his license for the number of in-person hours of coursework completed, upon providing a certificate of such participation from the course provider.</p><p>2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0227" target="_blank" rel="noreferrer">227</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0597" target="_blank" rel="noreferrer">597</a>.</p>',6),i=[n];function c(s,d,l,p,h,u){return r(),a("div",null,i)}const g=e(t,[["render",c]]);export{f as __pageData,g as default};
