import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse(`{"title":"§ 22.1-3.2 Notice of student's school status required as condition of admission; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-3.2.md","filePath":"vacode/22.1-3.2.md"}`),i={name:"vacode/22.1-3.2.md"},n=a('<h1 id="§-22-1-3-2-notice-of-student-s-school-status-required-as-condition-of-admission-penalty" tabindex="-1">§ 22.1-3.2 Notice of student&#39;s school status required as condition of admission; penalty. <a class="header-anchor" href="#§-22-1-3-2-notice-of-student-s-school-status-required-as-condition-of-admission-penalty" aria-label="Permalink to &quot;§ 22.1-3.2 Notice of student&#39;s school status required as condition of admission; penalty.&quot;">​</a></h1><p>A. Prior to admission to any public school of the Commonwealth, a school board shall require the parent, guardian, or other person having control or charge of a child of school age to provide, upon registration:</p><ol><li><p>A sworn statement or affirmation indicating whether the student has been expelled from school attendance at a private school or in a public school division of the Commonwealth or in another state for an offense in violation of school board policies relating to weapons, alcohol or drugs, or for the willful infliction of injury to another person. This document shall be maintained as a part of the student&#39;s scholastic record.</p></li><li><p>A sworn statement or affirmation indicating whether the student has been found guilty of or adjudicated delinquent for any offense listed in subsection G of § <a href="http://law.lis.virginia.gov/vacode/16.1-260/" target="_blank" rel="noreferrer">16.1-260</a> or any substantially similar offense under the laws of any state, the District of Columbia, or the United States or its territories. This document shall be maintained as provided in § <a href="http://law.lis.virginia.gov/vacode/22.1-288.2/" target="_blank" rel="noreferrer">22.1-288.2</a>.</p></li></ol><p>B. When the child is registered as a result of a foster care placement as defined in § <a href="http://law.lis.virginia.gov/vacode/63.2-100/" target="_blank" rel="noreferrer">63.2-100</a>, the information required under this section shall be furnished by the local social services agency or licensed child-placing agency that made the foster care placement.</p><p>C. Any person making a materially false statement or affirmation shall be guilty upon conviction of a Class 3 misdemeanor.</p><p>1993, c. 889; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0053" target="_blank" rel="noreferrer">53</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0183" target="_blank" rel="noreferrer">183</a>.</p>',6),r=[n];function s(l,c,d,h,f,p){return o(),t("div",null,r)}const g=e(i,[["render",s]]);export{m as __pageData,g as default};
