import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 22.1-318.1 Strategic Compensation Grant Fund established.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-318.1.md","filePath":"vacode/22.1-318.1.md"}'),r={name:"vacode/22.1-318.1.md"},s=n('<h1 id="§-22-1-318-1-strategic-compensation-grant-fund-established" tabindex="-1">§ 22.1-318.1 Strategic Compensation Grant Fund established. <a class="header-anchor" href="#§-22-1-318-1-strategic-compensation-grant-fund-established" aria-label="Permalink to &quot;§ 22.1-318.1 Strategic Compensation Grant Fund established.&quot;">​</a></h1><p>There is hereby created in the state treasury a special nonreverting fund to be known as the Strategic Compensation Grant Fund, hereafter referred to as &quot;the Fund.&quot; The Fund shall be established on the books of the Comptroller. All funds as may be appropriated for this purpose and any gifts, donations, grants, bequests, and other funds as may be received on its behalf shall be paid into the state treasury and credited to the Fund. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund. Moneys in the Fund shall be used solely for the purposes of awarding grants, pursuant to § <a href="http://law.lis.virginia.gov/vacode/22.1-318.2/" target="_blank" rel="noreferrer">22.1-318.2</a>, on a competitive basis to any local school division. Expenditures and disbursements from the Fund shall be made by the State Treasurer on warrants issued by the Comptroller upon written request signed by the Superintendent of Public Instruction.</p><p>2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0228" target="_blank" rel="noreferrer">228</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0691" target="_blank" rel="noreferrer">691</a>.</p>',3),o=[s];function i(d,l,h,c,u,p){return t(),a("div",null,o)}const b=e(r,[["render",i]]);export{_ as __pageData,b as default};
