import{_ as e,o,c as r,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 22.1-349.9 Employment of professional, licensed personnel.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-349.9.md","filePath":"vacode/22.1-349.9.md"}'),t={name:"vacode/22.1-349.9.md"},n=a('<h1 id="§-22-1-349-9-employment-of-professional-licensed-personnel" tabindex="-1">§ 22.1-349.9 Employment of professional, licensed personnel. <a class="header-anchor" href="#§-22-1-349-9-employment-of-professional-licensed-personnel" aria-label="Permalink to &quot;§ 22.1-349.9 Employment of professional, licensed personnel.&quot;">​</a></h1><p>A. College partnership laboratory school personnel are employees of the institution of higher education that establishes the school.</p><p>B. Teachers who work in a college partnership laboratory school shall hold a license issued by the Board or, in the case of an instructor in the Board-approved teacher education program of the institution of higher education, be eligible to hold a Virginia teaching license. Teachers working in a college partnership laboratory school are subject to the requirements of §§ <a href="http://law.lis.virginia.gov/vacode/22.1-296.1/" target="_blank" rel="noreferrer">22.1-296.1</a>, <a href="http://law.lis.virginia.gov/vacode/22.1-296.2/" target="_blank" rel="noreferrer">22.1-296.2</a>, and <a href="http://law.lis.virginia.gov/vacode/22.1-296.4/" target="_blank" rel="noreferrer">22.1-296.4</a> applicable to teachers employed by a local school board.</p><p>C. Professional, licensed personnel of a college partnership laboratory school shall be granted the same employment benefits given to professional, licensed personnel in public schools in accordance with the agreement between the college partnership laboratory school and the Board.</p><p>2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0871" target="_blank" rel="noreferrer">871</a>, § 23-299.8; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0170" target="_blank" rel="noreferrer">170</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',5),l=[n];function i(s,c,p,h,f,d){return o(),r("div",null,l)}const b=e(t,[["render",i]]);export{_ as __pageData,b as default};
