import{_ as o,o as t,c as a,z as e,a as s}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 22.1-35 School board selection commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-35.md","filePath":"vacode/22.1-35.md"}'),c={name:"vacode/22.1-35.md"},r=e("h1",{id:"§-22-1-35-school-board-selection-commission",tabindex:"-1"},[s("§ 22.1-35 School board selection commission. "),e("a",{class:"header-anchor",href:"#§-22-1-35-school-board-selection-commission","aria-label":'Permalink to "§ 22.1-35 School board selection commission."'},"​")],-1),i=e("p",null,"In each county to which the provisions of this article are applicable there shall be a school board selection commission composed of three members appointed from the county at large or, upon the request of the county governing body, one member appointed from each election district of such county. Members shall be qualified voters, shall reside in the county and shall not be county or state officers. Members shall be appointed by the circuit court of the county within thirty days after the first day of July, 1950, and every four years thereafter. Any vacancy occurring other than by expiration of term shall be filled by the circuit court within thirty days after the vacancy occurs. Each member shall receive twenty-five dollars for each day actually engaged in the performance of duties as such member, to be paid out of the funds of the school board. No person regularly employed by the school board of the division shall be eligible to serve on or as clerk of such school board selection commission.",-1),n=e("p",null,"Code 1950, § 22-60; 1956, c. 365; 1959, Ex. Sess., c. 79, § 1; 1972, cc. 224, 665; 1973, c. 275; 1980, c. 559.",-1),l=[r,i,n];function h(d,m,f,u,b,p){return t(),a("div",null,l)}const v=o(c,[["render",h]]);export{_ as __pageData,v as default};
