import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 22.1-350 Southwest Virginia Public Education Consortium created; region defined; governing board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-350.md","filePath":"vacode/22.1-350.md"}'),r={name:"vacode/22.1-350.md"},a=o('<h1 id="§-22-1-350-southwest-virginia-public-education-consortium-created-region-defined-governing-board" tabindex="-1">§ 22.1-350 Southwest Virginia Public Education Consortium created; region defined; governing board. <a class="header-anchor" href="#§-22-1-350-southwest-virginia-public-education-consortium-created-region-defined-governing-board" aria-label="Permalink to &quot;§ 22.1-350 Southwest Virginia Public Education Consortium created; region defined; governing board.&quot;">​</a></h1><p>A. The Southwest Virginia Public Education Consortium is hereby established and shall be referred to in this chapter as the Consortium. For the purposes of this chapter and the work of the Consortium, &quot;Southwest Virginia&quot; shall include the Counties of Bland, Buchanan, Carroll, Dickenson, Grayson, Lee, Russell, Scott, Smyth, Tazewell, Washington, Wise, and Wythe, and the Cities of Bristol, Galax, and Norton. The governing board of the Consortium shall consist of the school superintendents of the named localities; the chancellor or his designee of the University of Virginia&#39;s College at Wise; the presidents or their designees of Emory and Henry College, Virginia Intermont College, Bluefield College, Mountain Empire Community College, Virginia Highlands Community College, Southwest Virginia Community College, and Wytheville Community College; and the Director of the Southwest Virginia Higher Education Center. The region&#39;s legislators shall serve as nonvoting, advisory members of the board.</p><p>B. Members of the board shall be reimbursed for their actual expenses incurred in the performance of their duties in the work of the Consortium, except that legislative members shall be reimbursed pursuant to § <a href="http://law.lis.virginia.gov/vacode/30-19.16/" target="_blank" rel="noreferrer">30-19.16</a>. The board shall elect a chairman and a vice-chairman from among its members.</p><p>1992, c. 785; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0276" target="_blank" rel="noreferrer">276</a>; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0424" target="_blank" rel="noreferrer">424</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0437" target="_blank" rel="noreferrer">437</a>.</p>',4),n=[a];function s(l,h,d,c,g,u){return i(),t("div",null,n)}const p=e(r,[["render",s]]);export{f as __pageData,p as default};
