import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 22.1-36.1 Composition of school board in certain cases.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-36.1.md","filePath":"vacode/22.1-36.1.md"}'),r={name:"vacode/22.1-36.1.md"},i=a('<h1 id="§-22-1-36-1-composition-of-school-board-in-certain-cases" tabindex="-1">§ 22.1-36.1 Composition of school board in certain cases. <a class="header-anchor" href="#§-22-1-36-1-composition-of-school-board-in-certain-cases" aria-label="Permalink to &quot;§ 22.1-36.1 Composition of school board in certain cases.&quot;">​</a></h1><p>Notwithstanding any other provision of law, when a county contains a town that is a separate school division, the school board for such county, regardless of whether it is elected or appointed, shall have no member representing such town. Instead, the county school board shall be comprised of one member elected or appointed from all of the election districts other than districts which have more than five percent of town residents, and an additional member elected or appointed at large from the entire county, excluding the town.</p><p>1993, c. 220; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0316" target="_blank" rel="noreferrer">316</a>; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0146" target="_blank" rel="noreferrer">146</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0269" target="_blank" rel="noreferrer">269</a>.</p>',3),n=[i];function s(c,l,h,d,p,_){return o(),t("div",null,n)}const g=e(r,[["render",s]]);export{m as __pageData,g as default};
