import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 22.1-4.1 Street addresses required in certain school admission documents.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-4.1.md","filePath":"vacode/22.1-4.1.md"}'),s={name:"vacode/22.1-4.1.md"},o=a('<h1 id="§-22-1-4-1-street-addresses-required-in-certain-school-admission-documents" tabindex="-1">§ 22.1-4.1 Street addresses required in certain school admission documents. <a class="header-anchor" href="#§-22-1-4-1-street-addresses-required-in-certain-school-admission-documents" aria-label="Permalink to &quot;§ 22.1-4.1 Street addresses required in certain school admission documents.&quot;">​</a></h1><p>Documents submitted for admission of any child to public schools in the Commonwealth, except such documents required in accordance with §§ <a href="/vacode/22.1-3.1/">22.1-3.1</a> and <a href="/vacode/22.1-270/">22.1-270</a>, shall include the street address or route number of each pupil&#39;s residence. If no street address or route number exists for such residence, a post office box number shall be required.</p><p>If the pupil is a homeless child or youth as defined in subdivision A 7 of § <a href="/vacode/22.1-3/">22.1-3</a>, and for that reason the school division determines, on the basis of the affidavit of the person seeking to enroll the pupil, that a street address, route number, or post office box number cannot be provided, it may accept an address in an alternate form it deems appropriate.</p><p>Address information provided under this section shall not be released to any person unless otherwise authorized by law.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0900" target="_blank" rel="noreferrer">900</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0209" target="_blank" rel="noreferrer">209</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0500" target="_blank" rel="noreferrer">500</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0586" target="_blank" rel="noreferrer">586</a>.</p>',5),i=[o];function n(d,c,l,h,u,p){return r(),t("div",null,i)}const _=e(s,[["render",n]]);export{m as __pageData,_ as default};
