import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 22.1-7 Responsibility of each state board, agency, and institution having children in residence or in custody.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-7.md","filePath":"vacode/22.1-7.md"}'),n={name:"vacode/22.1-7.md"},o=i('<h1 id="§-22-1-7-responsibility-of-each-state-board-agency-and-institution-having-children-in-residence-or-in-custody" tabindex="-1">§ 22.1-7 Responsibility of each state board, agency, and institution having children in residence or in custody. <a class="header-anchor" href="#§-22-1-7-responsibility-of-each-state-board-agency-and-institution-having-children-in-residence-or-in-custody" aria-label="Permalink to &quot;§ 22.1-7 Responsibility of each state board, agency, and institution having children in residence or in custody.&quot;">​</a></h1><p>A. Each state board, state agency, and state institution having children in residence or in custody shall have responsibility for providing for the education and training to such children which is at least comparable to that which would be provided to such children in the public school system. Such board, agency, or institution may provide such education and training either directly with its own facilities and personnel in cooperation with the Board of Education or under contract with a school division or any other public or private nonreligious school, agency, or institution.</p><p>B. The Board of Education shall supervise the education and training provided to school-age individuals in state training centers, and shall provide for and direct the education for school-age individuals in state hospitals operated by the Department of Behavioral Health and Developmental Services in cooperation with the Department of Behavioral Health and Developmental Services.</p><p>C. The Board shall prescribe standards and regulations for all such education and training provided directly by a state board, state agency, or state institution.</p><p>D. Each state board, state agency, or state institution providing such education and training shall submit annually its program therefor to the Board of Education for approval in accordance with regulations of the Board.</p><p>E. If any child in the custody of any state board, state agency, or state institution is a child with disabilities as defined in § <a href="/vacode/22.1-213/">22.1-213</a> and such board, agency, or institution must contract with a private nonreligious school to provide special education as defined in § <a href="/vacode/22.1-213/">22.1-213</a> for such child, the state board, state agency, or state institution may proceed as a guardian pursuant to the provisions of subsection A of § <a href="/vacode/22.1-218/">22.1-218</a>.</p><p>F. Any person of school age who is admitted pursuant to § <a href="/vacode/16.1-338/">16.1-338</a>, <a href="/vacode/16.1-339/">16.1-339</a>, or <a href="/vacode/16.1-340.1/">16.1-340.1</a> or in accordance with an order of involuntary commitment entered pursuant to §§ <a href="/vacode/16.1-341/">16.1-341</a> through <a href="/vacode/16.1-345/">16.1-345</a> to a state facility for children and adolescents operated by the Department of Behavioral Health and Developmental Services shall, upon admission, be permitted to participate in any education program offered in the facility that is administered by the Department of Education, regardless of his enrollment status. Information required to enroll such person in any such education program may be disclosed in accordance with state and federal law. Nothing in this subsection shall be construed to require enrollment in an education program if such person has been excused from attendance at school pursuant to subdivision B 1 of § <a href="/vacode/22.1-254/">22.1-254</a>.</p><p>Code 1950, § 22-9.1:04; 1972, c. 603; 1974, c. 480; 1980, c. 559; 1985, c. 207; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0854" target="_blank" rel="noreferrer">854</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0928" target="_blank" rel="noreferrer">928</a>; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0173" target="_blank" rel="noreferrer">173</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0281" target="_blank" rel="noreferrer">281</a>.</p>',8),r=[o];function s(c,d,l,h,p,u){return a(),t("div",null,r)}const v=e(n,[["render",s]]);export{g as __pageData,v as default};
