import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 22.1-79.7 School meal policies; donations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-79.7.md","filePath":"vacode/22.1-79.7.md"}'),r={name:"vacode/22.1-79.7.md"},i=t('<h1 id="§-22-1-79-7-school-meal-policies-donations" tabindex="-1">§ 22.1-79.7 School meal policies; donations. <a class="header-anchor" href="#§-22-1-79-7-school-meal-policies-donations" aria-label="Permalink to &quot;§ 22.1-79.7 School meal policies; donations.&quot;">​</a></h1><p>A. Each local school board shall adopt policies that:</p><ol><li><p>Prohibit school board employees from requiring a student who cannot pay for a meal at school or who owes a school meal debt to throw away or discard a meal after it has been served to him, do chores or other work to pay for such meals, or wear a wristband or hand stamp;</p></li><li><p>Require school board employees to direct any communication relating to a school meal debt to the student&#39;s parent. Such policy may permit such communication to be made by a letter addressed to the parent to be sent home with the student;</p></li><li><p>Prohibit the school board from filing a lawsuit against a student or the student&#39;s parent because the student cannot pay for a meal at school or owes a school meal debt; and</p></li><li><p>Prohibit the school board or any school board employee from denying a student the opportunity to participate in any extracurricular school activity because the student cannot pay for a meal at school or owes a school meal debt.</p></li></ol><p>B. Any school board may solicit and receive any donation or other funds for the purpose of eliminating or offsetting any school meal debt at any time and shall use any such funds solely for such purpose.</p><p>2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0384" target="_blank" rel="noreferrer">384</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0509" target="_blank" rel="noreferrer">509</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0575" target="_blank" rel="noreferrer">575</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0106" target="_blank" rel="noreferrer">106</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0686" target="_blank" rel="noreferrer">686</a>.</p>',5),l=[i];function n(s,c,h,p,d,m){return o(),a("div",null,l)}const g=e(r,[["render",n]]);export{u as __pageData,g as default};
