import{_ as o,o as t,c as a,z as e,a as i}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 22.1-87 Judicial review.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/22.1-87.md","filePath":"vacode/22.1-87.md"}'),c={name:"vacode/22.1-87.md"},s=e("h1",{id:"§-22-1-87-judicial-review",tabindex:"-1"},[i("§ 22.1-87 Judicial review. "),e("a",{class:"header-anchor",href:"#§-22-1-87-judicial-review","aria-label":'Permalink to "§ 22.1-87 Judicial review."'},"​")],-1),r=e("p",null,"Any parent, custodian, or legal guardian of a pupil attending the public schools in a school division who is aggrieved by an action of the school board may, within thirty days after such action, petition the circuit court having jurisdiction in the school division to review the action of the school board. Such review shall proceed upon the petition, the minutes of the meeting at which the school board's action was taken, the orders, if any, of the school board, an attested copy of the transcript, if any, of any hearing before the school board, and any other evidence found relevant to the issues on appeal by the court. The action of the school board shall be sustained unless the school board exceeded its authority, acted arbitrarily or capriciously, or abused its discretion.",-1),n=e("p",null,"Code 1950, § 22-57; 1968, c. 139; 1980, c. 559; 1981, c. 229.",-1),d=[s,r,n];function h(l,u,p,f,_,v){return t(),a("div",null,d)}const m=o(c,[["render",h]]);export{y as __pageData,m as default};
