import{_ as e,o as t,c as i,O as n}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 23.1-101 Endowment funds of public institutions of higher education.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-101.md","filePath":"vacode/23.1-101.md"}'),r={name:"vacode/23.1-101.md"},o=n('<h1 id="§-23-1-101-endowment-funds-of-public-institutions-of-higher-education" tabindex="-1">§ 23.1-101 Endowment funds of public institutions of higher education. <a class="header-anchor" href="#§-23-1-101-endowment-funds-of-public-institutions-of-higher-education" aria-label="Permalink to &quot;§ 23.1-101 Endowment funds of public institutions of higher education.&quot;">​</a></h1><p>It is the public policy of the Commonwealth that:</p><ol><li><p>Each public institution of higher education, the Frontier Culture Museum of Virginia, Gunston Hall, the Jamestown-Yorktown Foundation, the Science Museum of Virginia, and the Virginia Museum of Fine Arts shall be encouraged in their attempts to increase their endowment funds and unrestricted gifts from private sources and reduce the hesitation of prospective donors to make contributions and unrestricted gifts; and</p></li><li><p>Consistent with § 10 of Chapter 33 of the Acts of Assembly of 1927, in measuring the extent to which the Commonwealth shall finance higher education in the Commonwealth, the availability of the endowment funds and unrestricted gifts from private sources received by public institutions of higher education, the Frontier Culture Museum of Virginia, Gunston Hall, the Jamestown-Yorktown Foundation, the Science Museum of Virginia, and the Virginia Museum of Fine Arts shall neither be taken into consideration in nor used to reduce state appropriations or payments and shall be used in accordance with the wishes of the donors of such funds to strengthen the services rendered by these institutions to the people of the Commonwealth.</p></li></ol><p>Code 1950, § 9-65.14, § 23-252; 1952, c. 707, § 23-9.2; 1956, c. 390; 1970, c. 466; 1972, c. 524; 1974, c. 124; 1977, c. 597; 1981, c. 505; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0367" target="_blank" rel="noreferrer">367</a>, § 23-253.7; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0589" target="_blank" rel="noreferrer">589</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0786" target="_blank" rel="noreferrer">786</a>, § 23-287; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0104" target="_blank" rel="noreferrer">104</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0125" target="_blank" rel="noreferrer">125</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0879" target="_blank" rel="noreferrer">879</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0650" target="_blank" rel="noreferrer">650</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0480" target="_blank" rel="noreferrer">480</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',4),a=[o];function s(l,c,h,u,f,g){return t(),i("div",null,a)}const m=e(r,[["render",s]]);export{p as __pageData,m as default};
