import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 23.1-1020 Covered institutions; operational authority; human resources; covered employees generally.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-1020.md","filePath":"vacode/23.1-1020.md"}'),i={name:"vacode/23.1-1020.md"},o=a('<h1 id="§-23-1-1020-covered-institutions-operational-authority-human-resources-covered-employees-generally" tabindex="-1">§ 23.1-1020 Covered institutions; operational authority; human resources; covered employees generally. <a class="header-anchor" href="#§-23-1-1020-covered-institutions-operational-authority-human-resources-covered-employees-generally" aria-label="Permalink to &quot;§ 23.1-1020 Covered institutions; operational authority; human resources; covered employees generally.&quot;">​</a></h1><p>A. Each covered employee shall continue to be a state employee who is governed by and eligible to participate in the human resources and benefits programs that governed him and in which he was eligible to participate immediately prior to the effective date of the initial management agreement for the covered institution by which he is employed, including the state retirement system, state health insurance program, state workers&#39; compensation coverage program, and state grievance procedure, until the covered institution establishes a human resources program or programs, plan, or procedure applicable to him pursuant to this article in any such human resources or benefits program area. If, however, a covered institution is permitted by law other than in this chapter to establish an alternative health insurance plan or an alternative faculty or University of Virginia Medical Center retirement plan, such alternative health insurance or faculty or University of Virginia Medical Center retirement plan shall apply to and govern the covered employees included in such plan.</p><p>B. All human resources programs, plans, policies, and procedures established by the governing board of a covered institution pursuant to this article shall apply to and govern all participating covered employees, except as provided in § <a href="http://law.lis.virginia.gov/vacode/23.1-1022/" target="_blank" rel="noreferrer">23.1-1022</a>.</p><p>C. All covered institutions are responsible for meeting the human resource reporting requirements established by the Governor and General Assembly.</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0933" target="_blank" rel="noreferrer">933</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0945" target="_blank" rel="noreferrer">945</a>, § 23-38.114; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',5),n=[o];function s(l,c,p,h,d,u){return r(),t("div",null,n)}const v=e(i,[["render",s]]);export{g as __pageData,v as default};
