import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 23.1-1024 Covered institutions; operational authority; human resources; miscellaneous personnel matters.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-1024.md","filePath":"vacode/23.1-1024.md"}'),r={name:"vacode/23.1-1024.md"},i=a('<h1 id="§-23-1-1024-covered-institutions-operational-authority-human-resources-miscellaneous-personnel-matters" tabindex="-1">§ 23.1-1024 Covered institutions; operational authority; human resources; miscellaneous personnel matters. <a class="header-anchor" href="#§-23-1-1024-covered-institutions-operational-authority-human-resources-miscellaneous-personnel-matters" aria-label="Permalink to &quot;§ 23.1-1024 Covered institutions; operational authority; human resources; miscellaneous personnel matters.&quot;">​</a></h1><p>A. Each covered institution shall base all appointments, promotions, and tenure decisions upon merit and fitness, to be ascertained, as far as possible, by the competitive rating of qualifications by that institution.</p><p>B. No establishment of a position or rate of pay or change in rate of pay shall become effective except on order of the appointing covered institution.</p><p>C. No current or prospective participating covered employee of any covered institution shall be required, as a condition of employment, to smoke or use tobacco products on the job or abstain from smoking or using tobacco products outside the course of his employment, provided that this subsection shall not apply to those classes of employees to which § <a href="http://law.lis.virginia.gov/vacode/27-40.1/" target="_blank" rel="noreferrer">27-40.1</a> or <a href="http://law.lis.virginia.gov/vacode/51.1-813/" target="_blank" rel="noreferrer">51.1-813</a> is applicable.</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0933" target="_blank" rel="noreferrer">933</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0945" target="_blank" rel="noreferrer">945</a>, § 23-38.118; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',5),n=[i];function s(l,c,p,u,h,d){return t(),o("div",null,n)}const _=e(r,[["render",s]]);export{f as __pageData,_ as default};
