import{_ as t,o as n,c as a,z as e,a as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 23.1-1226 Powers; issuance of negotiable notes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-1226.md","filePath":"vacode/23.1-1226.md"}'),s={name:"vacode/23.1-1226.md"},r=e("h1",{id:"§-23-1-1226-powers-issuance-of-negotiable-notes",tabindex:"-1"},[o("§ 23.1-1226 Powers; issuance of negotiable notes. "),e("a",{class:"header-anchor",href:"#§-23-1-1226-powers-issuance-of-negotiable-notes","aria-label":'Permalink to "§ 23.1-1226 Powers; issuance of negotiable notes."'},"​")],-1),i=e("p",null,"The Authority may issue negotiable notes for any corporate purpose or renew any notes by the issuance of new notes, whether or not the notes to be renewed have matured. The Authority may issue notes partly to renew notes or to discharge other obligations then outstanding and partly for any other purpose. Such notes may be authorized, sold, executed, and delivered in the same manner as bonds. Any resolution authorizing notes or any issuance of notes by the Authority may contain any provision that the Authority may include in any resolution authorizing revenue bonds or any issuance of revenue bonds by the Authority, and the Authority may include in any note any term, covenant, or condition that it may include in any bond. All such notes are payable solely from the revenues of the Authority, subject only to any contractual rights of the holders of any of its notes or other obligations then outstanding.",-1),c=e("p",null,[o("1972, c. 686, § 23-30.46; 2016, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588",target:"_blank",rel:"noreferrer"},"588"),o(".")],-1),h=[r,i,c];function l(u,d,y,f,p,b){return n(),a("div",null,h)}const g=t(s,[["render",l]]);export{_ as __pageData,g as default};
