import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 23.1-1502 Meetings; officers; committees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-1502.md","filePath":"vacode/23.1-1502.md"}'),o={name:"vacode/23.1-1502.md"},i=a('<h1 id="§-23-1-1502-meetings-officers-committees" tabindex="-1">§ 23.1-1502 Meetings; officers; committees. <a class="header-anchor" href="#§-23-1-1502-meetings-officers-committees" aria-label="Permalink to &quot;§ 23.1-1502 Meetings; officers; committees.&quot;">​</a></h1><p>A. The board shall meet at the University once a year and at such other times as it determines. Special meetings of the board may be called by the rector or any three members. The secretary shall provide notice of any special meeting to each member.</p><p>B. Eight members shall constitute a quorum.</p><p>C. Every other year, the board shall appoint from its membership a rector to preside at its meetings, a vice-rector to preside at its meetings in the absence of the rector, and a secretary to preside at its meetings in the absence of the rector and vice-rector.</p><p>D. The board may appoint a pro tempore officer to preside at its meetings in the absence of the rector, vice-rector, and secretary.</p><p>E. Vacancies in the offices of rector, vice-rector, and secretary may be filled by the board for the unexpired term.</p><p>F. At every regular annual meeting of the board, the board may appoint an executive committee for the transaction of business in the recess of the board, consisting of at least three and not more than five members, to serve for a period of one year or until the next regular annual meeting.</p><p>1972, c. 550, § 23-91.29; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',8),s=[i];function n(c,m,h,p,d,f){return t(),r("div",null,s)}const b=e(o,[["render",n]]);export{_ as __pageData,b as default};
