import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 23.1-1802 Meetings; officers; committees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-1802.md","filePath":"vacode/23.1-1802.md"}'),i={name:"vacode/23.1-1802.md"},o=a('<h1 id="§-23-1-1802-meetings-officers-committees" tabindex="-1">§ 23.1-1802 Meetings; officers; committees. <a class="header-anchor" href="#§-23-1-1802-meetings-officers-committees" aria-label="Permalink to &quot;§ 23.1-1802 Meetings; officers; committees.&quot;">​</a></h1><p>A. The board shall meet at the University once a year and at such other times as it determines.</p><p>B. A majority of the members shall constitute a quorum.</p><p>C. At the first meeting after July 1 in every even-numbered year, the board shall appoint from its membership a rector to preside at its meetings, a vice-rector to preside at its meetings in the absence of the rector, and a secretary to preside at its meetings in the absence of the rector and vice-rector.</p><p>D. The board may appoint a pro tempore officer to preside at its meetings in the absence of the rector, vice-rector, and secretary.</p><p>E. Vacancies in the offices of rector, vice-rector, and secretary may be filled by the board for the unexpired term.</p><p>F. Special meetings of the board may be called by the rector or any three members. In either case, the secretary shall give notice of the time of meetings to each member.</p><p>G. At every regular annual meeting of the board, it may appoint an executive committee for the transaction of business in the recess of the board, consisting of at least three and not more than five members, to serve for a period of one year or until the next regular annual meeting.</p><p>1972, c. 861, § 23-91.40; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0176" target="_blank" rel="noreferrer">176</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0195" target="_blank" rel="noreferrer">195</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',9),n=[o];function s(c,m,f,h,p,l){return t(),r("div",null,n)}const _=e(i,[["render",s]]);export{g as __pageData,_ as default};
